mapper.select:
select ss.id,ss.hospital_code,ss.bottle_code,ss.patient_info,ss.receive_time,ss.status,ss.userid,ss.sample_type_name, group_concat(ss.content) sample_content FROM ( select s.*,t.sample_type sample_type_name,r.content from samples s,sample_type t,sample_remark_rel srr,remarks r where s.sample_type=t.id and s.id=srr.sample_id and srr.remark_id=r.id UNION select s.*,t.sample_type sample_type_name,null from samples s,sample_type t where s.sample_type=t.id ) ss where 1=1
and ss.hospital_code = #{hospitalCode,jdbcType=VARCHAR}
and ss.sample_type <= #{bottleCode,jdbcType=VARCHAR}
and ss.sample_type=#{sampleType,jdbcType=INTEGER}
GROUP BY ss.id,ss.hospital_code,ss.bottle_code,ss.patient_info,ss.receive_time,ss.status,ss.userid,ss.sample_type_name order by ss.receive_time desc
limit #{rows,jdbcType=INTEGER} offset #{start,jdbcType=INTEGER}

select count(distinct ss.id) FROM ( select s.*,t.sample_type sample_type_name,r.content from samples s,sample_type t,sample_remark_rel srr,remarks r where s.sample_type=t.id and s.id=srr.sample_id and srr.remark_id=r.id UNION select s.*,t.sample_type sample_type_name,'' from samples s,sample_type t where s.sample_type=t.id ) ss where 1=1
and ss.hospital_code = #{hospitalCode,jdbcType=VARCHAR}
and ss.sample_type <= #{bottleCode,jdbcType=VARCHAR}
and ss.sample_type=#{sampleType,jdbcType=INTEGER}
